import React from 'react'
import {compose} from 'redux'

const ProjectDetails = (props) => {
    const {project} = props;
    if(project)
    return (
        <div className="container section project-details">
            <div className="card z-depth-0">
                <div className="card-content">
                    <span className="card-title">Project title - { id }</span>
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Sint doloremque sapiente dolor odio amet, cum voluptates debitis cumque molestiae iste illum assumenda corrupti pariatur ullam repellendus reprehenderit inventore? Distinctio, quae?</p>
                    <div className="card-action grey lighten-4 grey-text">
                        <div>Posted by The Net Ninja</div>
                        <div>2nd September, 2am</div>
                    </div>
                </div>
            </div>
        </div>
    )
}
const mapStateToProps = (state,ownProps) =>{
    //console.log(state);
    const id = ownProps.match.params.id;
    const projects = state.firestore.data.projects;
    const project = projects ? projects[id] : null
    return{
        project: project
    }
}
export default compose(
    connect(mapStateToProps),
    firestoreConnect([{
        collection:'projects'
    }])
)(ProjectDetails)
